  Future<void> fetchUserData(email) async {
    try {
      QuerySnapshot querySnapshot = await FirebaseFirestore.instance.collection('Users').where('email', isEqualTo: email).limit(1).get();

      if (querySnapshot.docs.isNotEmpty) {
        var userData = querySnapshot.docs.first;
        print("userData: $userData");
        nameController.text = userData['UserName'] ?? '';
        _previewProvider.updateName(userData['UserName'] ?? '');

        employerController.text = userData['Employer'] ?? '';
        _previewProvider.updateemployer(userData['Employer'] ?? '');

        divisionOrSectionController.text = userData['Division_or_Section'] ?? '';
        _previewProvider.updatedivision(userData['Division_or_Section'] ?? '');

        RoleController.text = userData['Role'] ?? '';
        _previewProvider.updaterole(userData['Role'] ?? '');

        LocationController.text = userData['Location'] ?? '';
        _previewProvider.updatelocation(userData['Location'] ?? '');

        EmployeeNumberController.text = userData['Employee_Number'] ?? '';
        _previewProvider.updateemployeeNumber(userData['Employee_Number'] ?? '');

        LineManagerController.text = userData['Line_Manager'] ?? '';
        _previewProvider.updatelinemanager(userData['Line_Manager'] ?? '');

        AboutMeLabeltextController.text = userData['UserName'] + " - draft communication to " + userData['Employer'] ;
        About_Me_Label = userData['UserName']  + " - draft communication to " + userData['Employer'] ;

      } else {
        // User not found
      }
    } catch (e) {
      print("Error fetching user data: $e");
    }
  }

  Future<void> duplicatePPStoOS(context,documentId, int AB_id,Purpose_of_report,employer,linemanager) async {
    try {
      CollectionReference collectionReference = FirebaseFirestore.instance.collection('AboutMe');

      DocumentSnapshot documentSnapshot = await collectionReference.doc(documentId).get();
      Map<String, dynamic> data = documentSnapshot.data() as Map<String, dynamic>;

      // Generate a new ID for the duplicated document
      var newDocumentId = collectionReference.doc().id;

      String message = """
Thank you to ${employer}....

… for recognising that our organisation performs better, and we achieve more together,when each of us feels safe and open to share what we need in order to be our best in the roles we are asked and agree to perform.

This document sets out what I think it would be helpful for you to know about me and includes information about what I believe helps me to thrive, so that I can perform to my  best, both for me and for ${employer}.

Next steps

I will arrange a meeting with my [you]/[occupational health]/[${linemanager}] to discuss myrequests in person, and to ascertain which are possible for me to action personally and which ${employer} is able to provide for me. My requests include workplaceaccommodations that I view as reasonable adjustments under the Equality Act 2010.

Thank you for being open to understanding me better and for considering my requests.

""";

      var createdAt = DateFormat('yyyy-MM-dd, HH:mm:ss').format(DateTime.now());

      data['AB_id'] = AB_id; // Update with the new value you want
      data['AB_Status'] = "Draft"; // Update with the new value you want
      data['Purpose_of_report'] = ""; // Update with the new value you want
      data['About_Me_Label'] = Purpose_of_report;
      data['isPPS'] = false;
      data['isOS'] = true;
      data['Report_sent_to'] = []; // Update with the new value you want
      data['Report_sent_to_cc'] = []; // Update with the new value you want
      data['Created_Date'] = createdAt; // Update with the new value you want
      data['AB_Description'] = message; // Update with the new value you want

      // Save the duplicated data with the new ID
      await collectionReference.doc(newDocumentId).set(data);

      print(' after collectionReference.doc(newDocumentId).set(data)');
      print('newDocumentId: ${newDocumentId}');

      DocumentSnapshot duplicatedDocumentSnapshot = await collectionReference.doc(newDocumentId).get();
      Map<String, dynamic> duplicatedData = duplicatedDocumentSnapshot.data() as Map<String, dynamic>;

      print('duplicatedDocumentSnapshot: ${duplicatedDocumentSnapshot.id}');
      print('duplicatedData: ${duplicatedData}');


      // await showUserLogedInEditAboutMeDialogBox(duplicatedDocumentSnapshot, 0);

      // _previewProvider.isDuplicate = true;

      // Notify the user that the duplication was successful
      toastification.show(context: context,
          title: Text('${duplicatedData["About_Me_Label"]} is saved to library'),
          autoCloseDuration: Duration(milliseconds: 2500),
          alignment: Alignment.center,
          backgroundColor: Colors.green,
          foregroundColor: Colors.white,
          icon: Icon(Icons.check_circle, color: Colors.white,),
          animationDuration: Duration(milliseconds: 1000),
          showProgressBar: false
      );
      // ScaffoldMessenger.of(context).showSnackBar(
      //   SnackBar(content: Text('Official submission created successfully!'), backgroundColor: Colors.green),
      // );
    } catch (error) {
      // Handle any errors that occur
      print('Error duplicating document: $error');
      ScaffoldMessenger.of(context).showSnackBar(
        SnackBar(content: Text('Error duplicating document. Please try again later.'), backgroundColor: Colors.red),
      );
    }
  }

