        crossAxisAlignment: pw.CrossAxisAlignment.start,
        children: [
          leading,
          pw.SizedBox(width: 8),
          pw.Expanded(
            child: pw.Column(
              crossAxisAlignment: pw.CrossAxisAlignment.start,
              children: [
                title,
                if (subtitle != null) subtitle,
